const StateTutorial = () => {
    const [counter, setCounter] = useState(0);

    const increment = () => {
        setCounter(counter + 1);
    };

    const decrement = () => {
        setCounter(counter - 1)
    };

    return (
        <div>
            <button onClick={decrement}>Remove</button>
            {counter}
            <button onClick={increment}>Add</button>
        </div>
    ) 


}

